import { useState, useEffect } from "react";
import { inAxios } from "../config_axios";
import ItemLista from "./ItemLista";

const ManutencaoLivros = () => {
  const [livros, setLivros] = useState([]);
  const [pesquisa, setPesquisa] = useState("");

  const obterLista = async () => {
    try {
      alert("Obtendo lista de livros..."); // Alerta para depuração
      const lista = await inAxios.get("livros");
      alert("Dados obtidos: " + JSON.stringify(lista.data)); // Alerta para depuração
      setLivros(lista.data);
    } catch (error) {
      alert(`Erro... Não foi possível obter os dados: ${error}`);
    }
  };

  const handlePesquisa = async (event) => {
    event.preventDefault();
    try {
      alert(`Pesquisando livros com título: ${pesquisa}`); // Alerta para depuração
      const lista = await inAxios.get(`livros?titulo=${pesquisa}`);
      alert("Dados da pesquisa: " + JSON.stringify(lista.data)); // Alerta para depuração
      setLivros(lista.data);
    } catch (error) {
      alert(`Erro... Não foi possível obter os dados: ${error}`);
    }
  };

  const handleTodos = () => {
    setPesquisa("");
    obterLista();
  };

  useEffect(() => {
    obterLista();
  }, []);

  useEffect(() => {
    alert("Livros no estado: " + JSON.stringify(livros)); // Alerta para depuração
  }, [livros]);

  return (
    <div className="container">
      <div className="row">
        <div className="col-sm-7">
          <h4 className="fst-italic mt-3">Manutenção</h4>
        </div>
        <div className="col-sm-5">
          <form onSubmit={handlePesquisa}>
            <div className="input-group mt-3">
              <input
                type="text"
                className="form-control"
                placeholder="Título ou autor"
                value={pesquisa}
                onChange={(e) => setPesquisa(e.target.value)}
                required
              />
              <input
                type="submit"
                className="btn btn-primary"
                value="Pesquisar"
              />
              <input
                type="button"
                className="btn btn-danger"
                value="Todos"
                onClick={handleTodos}
              />
            </div>
          </form>
        </div>
      </div>
      <table className="table table-striped mt-3">
        <thead>
          <tr>
            <th>Cód.</th>
            <th>Título</th>
            <th>Autor</th>
            <th>Ano</th>
            <th>Preço</th>
            <th>Foto</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {livros.map((livro) => (
            <ItemLista
              key={livro.id}
              id={livro.id}
              titulo={livro.titulo}
              autor={livro.autor}
              ano={livro.ano}
              preco={livro.preco}
              foto={livro.foto}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManutencaoLivros;
