import { useState, useEffect } from "react";
import Chart from "react-google-charts";
import { inAxios } from "../config_axios";

const ResumoLivros = () => {
  const [resumo, setResumo] = useState({});
  const [grafico, setGrafico] = useState([]);

  const obterDados = async () => {
    try {
      const dadosResumo = await inAxios.get("livros/dados/resumo");
      console.log("Dados Resumo:", dadosResumo.data);
      setResumo(dadosResumo.data);

      const dadosGrafico = await inAxios.get("livros/dados/resumo");
      console.log("Dados Gráfico:", dadosGrafico.data);

      const arrayGrafico = [
        ["Ano de Publicação", "Preço acumulado R$"],
        ["Número de Livros", dadosGrafico.data.num],
        ["2014", dadosGrafico.data.soma],
        ["Maior Preço", dadosGrafico.data.maior],
        ["Preço Médio", dadosGrafico.data.media],
      ];
      console.log("Array Gráfico:", arrayGrafico);
      setGrafico(arrayGrafico);
    } catch (error) {
      alert(`Erro... Não foi possível obter os dados: ${error}`);
    }
  };

  useEffect(() => {
    obterDados();
  }, []);

  return (
    <div className="container">
      <h4 className="mt-3">Resumo</h4>
      <span className="btn btn-outline-primary btn-lg">
        <p className="badge bg-danger">{resumo.num}</p>
        <p>N de Livros cadastrados</p>
      </span>
      <span className="btn btn-outline-primary btn-lg mx-2">
        <p className="badge bg-danger">
          {Number(resumo.soma).toLocaleString("pt-br", {
            minimumFractionDigits: 2,
          })}
        </p>
        <p>Total Investido em Livros</p>
      </span>
      <span className="btn btn-outline-primary btn-lg me-2">
        <p className="badge bg-danger">
          {Number(resumo.maior).toLocaleString("pt-br", {
            minimumFractionDigits: 2,
          })}
        </p>
        <p>Maior Preço Cadastrado</p>
      </span>
      <span className="btn btn-outline-primary btn-lg">
        <p className="badge bg-danger">
          {Number(resumo.media).toLocaleString("pt-br", {
            minimumFractionDigits: 2,
          })}
        </p>
        <p>Preço Médio dos Livros</p>
      </span>

      <div className="d-flex justify-content-center">
        <Chart
          width={1000}
          height={420}
          chartType="ColumnChart"
          loader={<div>Carregando Gráfico...</div>}
          data={grafico}
          options={{
            title: "Resumo dos Livros",
            chartArea: { width: "80%" },
            hAxis: { title: "Tipo" },
            vAxis: { title: "Valor" },
            legend: { position: "none" },
          }}
        />
      </div>
    </div>
  );
};

export default ResumoLivros;
